insert  into competency_class(id,name) values (5,'个人效能');
insert into competency (id, name, definition, description, class_id) values('1','远见卓识','有远大的眼光和卓越的见解，积极审视组织内外部环境的变化，把握组织（时代）发展变化的趋势，以有效洞察、预测与定位经营形势。','有终局思维：能够从零散的信息中，敏锐地洞察社会、行业以及市场等的新动向、新趋势，思考技术发展对商业文明可能的影响；全面考虑主要行动和决策对未来的影响；','1');
insert into competency (id, name, definition, description, class_id) values('2','洞察趋势与商机','通过分析所收集到的商业领域的相关数据与信息，从中觉察商业发展的动向，洞悉他人未曾发现的机会或者机遇。','具有独特的业务洞察力，能从人们消费习惯变化的信息中准确锁定他人不易觉察的机遇；并判断分析出潜在的发展机会；','1');
insert into competency (id, name, definition, description, class_id) values('3','战略定位与策略规划','确定组织在什么领域和用什么方式才能最大化的创造长期价值；针对趋势变化、市场需求以及竞争态势等因素，确立最具优势的有利位置，制定并坚持一连串长期行动路线，以实现长期目标或远见。','理解你的目标与比较优势，与市场的发展趋势和客户的痛点有机结合，锁定提供有价值的产品或者服务的方向；','1');
insert into competency (id, name, definition, description, class_id) values('4','资源统驭','重视资源的优化配置和投入产出比，系统、灵活地采集、调动和配置资源，最高限度地提高资源的利用效率，促进资源的增值和发展。','具备开阔的视野和敏锐的直觉，能够洞察内外部各种资源的价值、需求和增值前景；','1');
insert into competency (id, name, definition, description, class_id) values('5','果敢决策','在面对复杂情况时，能迅速分析并及时做出决策，保持团队“阵脚不乱”，使得团队成员迅速获得行动的方向感。','在关键时刻和重大关头，保持清醒的头脑，能够凭经验和直觉，当机立断地做出决策，并勇于承担决策风险；','1');
insert into competency (id, name, definition, description, class_id) values('6','开创新局面','积极面对并客观分析不断变化的内外部环境，主动发起变革，打破旧有局面与困境，选准切入点，实现经营突破。','勇于创新和变革，通过主动变革商业模式、管理模式，引领行业发展潮流乃至社会发展潮流；','1');
insert into competency (id, name, definition, description, class_id) values('7','破局（选准切入点）','冷静分析存在的问题，层层抽丝剥茧；选准切入点，庖丁解牛，打破困境，实现经营突破。','在公司经营管理陷入困局时，冷静分析存在问题，层层抽丝剥茧；能策划战役并赢得胜利；','1');
insert into competency (id, name, definition, description, class_id) values('8','迭代','秉持开放和互动的理念，积极寻求用户反馈、响应用户需求，勇于试错并改进完善产品，持续多个迭代周期开展创新的过程。','注重速度效率、持续改进优化、加强与用户的直接互动，黏住用户，将其反馈作为设计决策依据；','1');
insert into competency (id, name, definition, description, class_id) values('9','创造性解决问题','基于特定的问题情境，在问题的定义和备选方案的提出上给自己更大的灵活性，应用各种认知活动与技能，经过一系列的思维与操作，使问题得以解决。','鼓励提出各种备选解决方案，并对每一方案的利弊做出合理评估，迅速缩小“最优解决方案”的范围；','1');
insert into competency (id, name, definition, description, class_id) values('10','推动积极的变革','积极面对并快速适应由于不断变化的业务环境而导致的企业变革，整合资源推行并实施企业的变革，积极提供变革实施信息和实施改进建议。','利用适当的沟通途径和媒介，在变革过程中与员工持续沟通变革产生的影响和进展状况，并为员工提供过渡期中所必需的帮助，获得员工对变革的理解和支持；','1');
insert into competency (id, name, definition, description, class_id) values('11','市场与客户导向','树立以客户为中心、竭力为客户服务的意识，准确了解、创造并快速有效地满足客户需求，为客户创造价值，赢得客户认可。','不断跟踪行业最新的产品和服务发展趋势，立足自身优势实施产品和服务创新，从而激发客户需求；','1');
insert into competency (id, name, definition, description, class_id) values('12','数据（资讯）收集与分析','能够通过各种方式获取所需要的信息，并把零散的原始材料通过归纳、整理与综合分析，转化为系统的、具有较强操作性和指导性的意见与建议。','善于借助各种渠道和途径来获取想要的信息，甚至通过一些大部分人意想不到的渠道或对象获取信息；','1');
insert into competency (id, name, definition, description, class_id) values('13','创造客户价值','分析客户所处的环境、面临的问题，前瞻性地预测客户需求，不断驱动产品和服务的改进和创新，激发客户潜在需求、催生客户新需求、提升客户需求层次、增加客户需求多样性。','通过获取客户的第一手资料，在对客户的业务和面临的问题有透彻了解的基础上，从专业角度，帮助客户发现真正的需要（这些需要往往并没有体现在客户自己提出的要求中），帮助客户设计出符合其特点的个性化服务方案；','1');
insert into competency (id, name, definition, description, class_id) values('14','有效认知并满足客户需求','善于换位思考，乐于倾听，以收集有效的客户资讯，准确了解客户需求，并快速响应客户的需求，及时、合适地为客户提供产品和服务，解决客户的问题。','对客户的需求能迅速响应，以真诚、负责的态度及时解决客户提出的问题，并为客户提供满意的产品和服务；','1');
insert into competency (id, name, definition, description, class_id) values('15','赢领目标','抱有必赢的决心和信心，对于每一个目标，都充满了赢的渴求，力求胜人一筹。','敢于在行业中、市场中胜出，不畏惧强大的对手，敢于采取行动与强大的对手展开竞争，并力保胜出，力求成为所在领域的标杆；','2');
insert into competency (id, name, definition, description, class_id) values('16','计划制定与推进','能够准确的把握上级思路或组织的整体战略规划，并以此为依据，形成工作目标，制定具体的、可操作的行动方案，并有效组织各类资源，使计划顺利推行下去。','在计划实施过程中，进行适当监控和指导，对责任做具体落实，对方案计划的执行效果进行跟踪、反馈和改进。','2');
insert into competency (id, name, definition, description, class_id) values('17','成本意识','在所有付出努力的工作中，能够有在保证目标达成的前提下尽可能减少成本，或是在同样的成本投入上产生更大的价值的意识。','在已有的预算政策或资源条件下，通过与同事或其他人合作，灵活创新地寻找机会以节约成本或增加产出，使边际利润达到最大化；','2');
insert into competency (id, name, definition, description, class_id) values('18','持续改善','为自己设定更高的工作目标，积极主动地对每个过程和结果进行不断反思与分析，找到并实施各项措施以不断改善过程与结果。','定期对任务执行的方法、流程、结果等进行检查，将检查结果以及改进期望准确地反馈给相关人员；','2');
insert into competency (id, name, definition, description, class_id) values('19','注重品质与精确','重视产品和服务的品质，关注过程与结果各方面的细节，严格控制各种风险与隐患，降低不确定性和减少偏差，为社会、客户或同事提供极致的产品和服务。','依照计划表监控专案进度，检视资料，发现缺点或漏掉的资料，以及整体地增进现有系统的秩序性；','2');
insert into competency (id, name, definition, description, class_id) values('20','关注细节','在工作中，注重具体的问题，在考虑到全局的时候，同样深入地了解把握关键细节，以细节的完美作为重要的努力方向。','督促下属对工作各个环节进行多角度、全方位的考虑，确保计划的严密性；','2');
insert into competency (id, name, definition, description, class_id) values('21','风险防控','分析、预见并预防可能对经营或组织造成重大损失的不确定因素。','处理每项事务会充分考虑不确定因素，学习并使用系统化的风险识别、分析、预防及管理工具与技能；严格按照组织风险管理制度，开展规范业务操作，避免风险；','2');
insert into competency (id, name, definition, description, class_id) values('22','专业能力','对专业领域内所涉及的知识、原理、方法和流程等有良好的领悟力与驾驭能力。','对自己的专业能力有充分的信心，面对挑战时，相信自己的判断和决定；','2');
insert into competency (id, name, definition, description, class_id) values('23','技术热忱','对利用技术实现自己的价值有强烈的愿望，重视技术价值，追求技术精进，并将最新技术应用于实践中。','对自己所从事的技术工作有浓厚的兴趣，争取在所从事的技术领域中获得成就，并获得业界的认可；','2');
insert into competency (id, name, definition, description, class_id) values('24','高效执行','积极主动，采取行动，确保按时、保质、保量达成既定目标，并通过规范化和流程管理，提升执行的效率，高效达成目标。','遇到困难和阻碍时能够勇往直前，积极寻求解决办法，推动组织和部门工作目标的实现。','2');
insert into competency (id, name, definition, description, class_id) values('25','行动导向（敏捷）','聚焦机会，强调效率，不因纠结于责任追究或过度追求完美的计划而延缓行动，以积极的行动推进事情向前发展。','以行动为导向，聚焦机会，强调效率，不因过度追求完美而延缓行动；','2');
insert into competency (id, name, definition, description, class_id) values('26','要事优先','确定重要的事情，集中精力与时间去完成它。','能通过是否能为组织做出贡献或影响组织所期望的成果来识别什么是重要的事情和需要高度优先解决的事情；','2');
insert into competency (id, name, definition, description, class_id) values('27','规范化','对制度和秩序心存敬畏，要求自己和他人严格执行组织规定的各项流程、制度和规范，并制订和优化各项流程、规则、制度和规范。','强烈的规范意识，对制度和秩序心存敬畏，要求自己与组织成员严格执行组织规定的各项流程、制度和规范；','2');
insert into competency (id, name, definition, description, class_id) values('28','监督','对布置给下属的工作进行督促与跟进，并根据情况对下属的工作提供指导与支持，促使其有效完成工作任务，并提升工作能力。','在任务执行过程中，注意对工作进程的跟踪，能够及时获得信息反馈，迅速调整方案和策略，随时准备应对各种障碍和问题；','2');
insert into competency (id, name, definition, description, class_id) values('29','建设高效团队','建立科学合理的团队组织结构、流程和制度，正确评价团队成员的能力和风格，促进优势互补和风格互补，并为了组织的可持续发展而致力于学习型组织的建设。','科学设计团队中的业务流程、工作职责，以发挥每个人的最大能力；','3');
insert into competency (id, name, definition, description, class_id) values('30','知人善任','能够深入的了解他人的特点、优势、特长以及成熟度，能够识别和发掘蕴涵在组织成员身上的潜能，使其最大限度地发挥优势，实现个体与组织共同成长的目标。','通过各种渠道充分了解团队成员的价值观、兴趣、性格和能力，熟知他们的长处和不足，赋予合适的团队角色和职责；','3');
insert into competency (id, name, definition, description, class_id) values('31','创建协力','通过团队文化塑造、团队价值观建设，在团队内部培育相互协作、相互支持的氛围，从而提升成员对团队的归属感和凝聚力。','通过团队文化建设或其他的团队活动不断提升团队的凝聚力；增强团队成员的团队归属感和荣誉感；','3');
insert into competency (id, name, definition, description, class_id) values('32','培养人才','吸引、发展和留住人才；创造学习的环境，确保员工发挥最高潜能，确保组织整体能力能够应对未来挑战。','重视培训和职业发展通道的设计，为员工实践所学创造更多机会，使得员工有更大的成长空间；','3');
insert into competency (id, name, definition, description, class_id) values('33','组织承诺','在态度和价值观上认同组织，将个人的行为调整到与组织的价值追求、重要决定以及目标相一致，并在行动上致力于组织目标的达成。','站在组织全局利益的角度对待问题，自觉地通过切实有效的行动，补充制度流程、上级决策、同事行动、下属执行等方面的疏漏和缺欠；','3');
insert into competency (id, name, definition, description, class_id) values('34','赋能','让有潜力的员工获得资源、平台去为组织作出更大的贡献','建立能留任优秀人才的组织系统；让组织能灵活满足个人需求；为优秀员工提供清晰的职涯发展规划，提供职业挑战和职涯满意度。','3');
insert into competency (id, name, definition, description, class_id) values('35','指导与教练','关注下属的潜能与可塑性，希望看到下属能力的提升，并通过系统、有效的方式来教育培养下属，帮助他们成长。','了解下属的职业发展期望和他们的特点，给予针对性的指导和建设性意见，积极提供信息反馈，帮助他们更快成长；','3');
insert into competency (id, name, definition, description, class_id) values('36','激励人心','激发、引导和维持他人的工作热情，鼓励他人积极主动地工作，保持高昂士气。','通过职责赋予、制度保证等方法手段，使组织成员切实参与到组织发展中来，从而获得强烈的参与感、成就感、主人翁感和荣誉；','3');
insert into competency (id, name, definition, description, class_id) values('37','授权','将工作职责与职权赋予他人，并给他们自主决定的自由和权力以开展工作。','信任下属，相信他们能够自主完成任务；放手让有能力的组织成员独挡一面，从而使自己可以从日常事务中脱身出来关注全局性的工作；','3');
insert into competency (id, name, definition, description, class_id) values('38','共启愿景','凝炼组织成员内心深处共同的意象，借助生动的、鼓舞人心的交流方式，使愿景成为组织成员关注的焦点，激发和鼓舞团队的奋斗激情。','善于描绘有吸引力的愿景或核心目标，并确保组织成员的理解和认同，然后转化为具体的可执行的行动计划；','3');
insert into competency (id, name, definition, description, class_id) values('39','影响力','尊重他人，通过塑造自身的人格魅力，从而赢得他人尊重，进而影响他人并使他人信服。','能够根据环境场合的差异、沟通对象的职业、性别和个性等不同，随机应变地采取不同的表达方式和策略；','4');
insert into competency (id, name, definition, description, class_id) values('40','诚信','信守诺言、坦诚对人、行为正派，能获取他人的信任。','遵守诺言，言出必行，如果承诺的事情没有做到，会及时做出解释、说明，第一时间进行调整和补救；','4');
insert into competency (id, name, definition, description, class_id) values('41','自律（以身作则）','严格要求与约束自己，对自己坚持高要求、高标准而不受外界诱惑与干扰。','对自身有较高的道德标准，并能时时提醒约束自己的行为；','4');
insert into competency (id, name, definition, description, class_id) values('42','原则性（公平公正）','严格按照制度规范操作，坚持原则，坚决制止违反规则的行为。','以身作则，遵守组织的各项规章制度；涉及个人利益时仍然按照制度和原则处理，不为个人谋取私利；','4');
insert into competency (id, name, definition, description, class_id) values('43','同理心（知彼解己）','能够打破以自我为中心的思维模式，尝试从对方的角度和立场考虑问题，体察对方感受，促进相互理解。','体察他人的情绪、感受，能够通过表情、语气和肢体等非语言信息，准确判断和体认他人的情绪与情感状态；','4');
insert into competency (id, name, definition, description, class_id) values('44','积极的情绪互动','觉察和理解他人情绪，以及与他人情绪形成积极互动的能力。','善于创造积极的引导交流气氛以及表露对他人观点和感情的理解，以此赢得信任；','4');
insert into competency (id, name, definition, description, class_id) values('45','有效表达','进行清晰表达，有效地传达与交流信息。','根据环境场合的差异、沟通对象的职业、性别和个性等不同，随机应变地采取不同的表达方式和策略；','4');
insert into competency (id, name, definition, description, class_id) values('46','双赢思维','以积极的心态、不懈的努力对待与客户合作中产生的冲突与问题，致力于与客户建立持久、共赢的合作关系。','与客户一同发展，努力成为客户成长过程中的朋友和伙伴，关心客户的发展和困难，通过向客户提供可能的支持和帮助，实现企业与客户的双赢；','4');
insert into competency (id, name, definition, description, class_id) values('47','关系建立','与他人建立和保持友好、互利的关系或关系网。','针对不同情境和不同交往对象，灵活使用多种人际技巧和方式，以适应复杂的人际环境；','4');
insert into competency (id, name, definition, description, class_id) values('48','谈判能力（统合综效）','与客户、供应商或其他人进行商务洽谈、磋商、交涉和讨价还价的能力。','熟悉谈判时可能遇到的难点以及可以采用的解决方法，娴熟地应用各种谈判技巧；','4');
insert into competency (id, name, definition, description, class_id) values('49','冲突管理','管理人与人之间的分歧与差异，有效消除对立或抵触。','在冲突的情况下尽量弱化冲突双方的差异，更强调双方的共同利益，以达成一种和解的局面；','4');
insert into competency (id, name, definition, description, class_id) values('50','支持与服务','能够根据组织实际情况，努力为其他部门提供支持与服务，并将自己所带领的团队建设成为其他部门的支持后盾。','迅速响应其他部门的需求，以真诚、负责的态度及时解决其他部门提出的问题，并为其他部门提供满意的专业服务和支持；','4');
insert into competency (id, name, definition, description, class_id) values('51','沉着冷静','镇定、稳重，有自己的思路，不轻易受外界条件的影响。','清晰地意识到自己的行为，并得当地控制言谈举止；有效控制自己的不良情绪，保持积极乐观的心态，不会采取消极的行动；','5');
insert into competency (id, name, definition, description, class_id) values('52','承压能力','在压力面前保持耐心与精力集中，能承受与化解工作或生活中遇到的压力。','在逆境和压力下能有效调整思路和情绪，专注于如何解决问题；','5');
insert into competency (id, name, definition, description, class_id) values('53','弹性（适应性）','具有能适应不同环境、不同个性或不同人群，并有效工作的能力。','对挫折有客观正确的认识，能够经受挫折打击与压力，用积极的方式解决挫折问题，保持心理平衡和正常的行为，不易被强烈的情绪所困扰。','5');
insert into competency (id, name, definition, description, class_id) values('54','自信','相信自己有能力通过有效的手段完成某项任务、解决某个问题的信念。','相信自己有能力实现既定目标，表现出对自己决定或判断的认可；','5');
insert into competency (id, name, definition, description, class_id) values('55','尽责（主动补台）','能够认识到自己的工作在组织中的重要性，把实现组织的目标当成是自己的目标，并愿意承担起相关的责任，热爱自己的工作，乐于奉献。','觉察到组织的某些问题，虽然不是自己职责范围的事情，但是能处理的积极处理，不能处理的就积极反映，致力于问题的解决而非责任的划分；','5');
insert into competency (id, name, definition, description, class_id) values('56','主动性','在没有外部要求或其他人鼓励的情况下，主动寻求适宜的方法并采取必要行动的能力。','对工作热情投入，不断采取行动以推动事情进展，对出色完成任务、取得工作成果有强烈的渴望；','5');
insert into competency (id, name, definition, description, class_id) values('57','追求卓越（成就动机）','以几近偏执的态度苛求产品、管理、品味的精细化，为社会、为客户、为自己提供极致唯一的产品和服务。','挑战在每个领域所发现的平庸之人或平庸的成果，督促个人和团队把所能取得的最好成果作为最终目标；','5');
insert into competency (id, name, definition, description, class_id) values('58','经验开放性（突破性思维）','拥有开放的心态，不受陈规和以往经验的束缚，以新形势发展的要求为标准，对商业模式、价值创造模式、管理模式等进行创造性思考，提出与众不同的观点、见解和方法。','主动寻找与同行建立业务交流的机会，开创新的手段和方法掌握最新行业或专业资讯；','5');
insert into competency (id, name, definition, description, class_id) values('59','反思与提升','不满足已有成绩，不断反思自己与高标准的差距，善于总结成功和失败的经验，寻找提高自己能力的途径。','以发展为目的，对自己的表现做出客观、理性的评价，针对自身差距，设定明确的学习目标，围绕学习目标，自发地有计划地落实学习行动；','5');
insert into competency (id, name, definition, description, class_id) values('60','学习能力','通过各种有效的途径、方法和工具，增加自己对新东西的吸收、掌握和记忆。','通过把握学习内容的本质和规律，在新的学习内容与自己已有的知识结构之间寻找关联，通过知识网络化的方式掌握新知识；','5');
insert into competency (id, name, definition, description, class_id) values('61','跨界学习','跨多个专业或行业进行知识获取，进而创造新的方法、知识的过程。','主动探寻行业发展的新趋势，留意本行业或相关行业的新概念、新思维以及其他创新动向；','5');
